    // const [transferred, setTransferred] = useState(0);

    const {colors} = useTheme();
    const [username, SetUsername] = useState('')
    const [password, SetPassword] = useState('')
    const [confirmpassword, SetConfirmPassword] = useState('')
    const [firstname, SetFirstname] = useState('')
    const [lastname, SetLastname] = useState('')
    const [email, SetEmail] = useState('')
    const [phonenumber, SetPhoneNumber] = useState('')

    const agree = false;
    const [showModal = false, SetShowModal] = useState()

    const theme = useTheme();

    const buttonTextStyle = {
        color: '#FFF'
    };

    useEffect(() => {
        SetFirstname("");
        SetLastname("");
        SetUsername("");
        SetPassword("");
        SetConfirmPassword("");
        SetEmail("");
        SetPhoneNumber("");
    }, []);

    async function RegisterUser() {
        await
        auth()
            .createUserWithEmailAndPassword(email, password)
            .then((user) => {
                user
                    .user
                    .updateProfile({displayName: username})
                firestore()
                    .collection('users')
                    .doc(user.user.uid)
                    .set({
                        username: username,
                        firstname: firstname,
                        lastname: lastname,
                        phonenumber: phonenumber,
                        role: 'user',
                        gender: ''
                    })
                console.log('User created login!')
                navigation.navigate('ข้อตกลง')
            })
            .catch((error) => {
                alert(error)
            })

        }

    return (
        <View style={styles.container}>
            <View style={styles.topTab}>
                <View style={styles.row}>
                    <TouchableOpacity
                        onPress={() => {
                            navigation.goBack()
                        }}>
                        <Icon name="chevron-left" color={"#FFF"} size={20} style={styles.optionTab}/>
                    </TouchableOpacity>
                    <View style={styles.ViewTitleOnTab}>
                        <Text style={styles.titleOnTab}>สมัครสมาชิก</Text>
                    </View>
                </View>
            </View>

            <ProgressSteps activeStep={0}>
                <ProgressStep
                    label="ข้อมูลผู้ใช้"
                    nextBtnTextStyle={buttonTextStyle}
                    previousBtnTextStyle={buttonTextStyle}
                    nextBtnText="ถัดไป">
                    <View
                        style={{
                            alignItems: 'center'
                        }}>

                        <PhotoUpload
                            onPhotoSelect={avatar => {
                                if (avatar) {
                                    console.log('Image base64 string: ', avatar)
                                }
                            }}>
                            <Image
                                style={{
                                    paddingVertical: 30,
                                    width: 100,
                                    height: 100,
                                    borderRadius: 75
                                }}
                                resizeMode='cover'
                                source={require('../assets/images/upload_image.png')}/>
                        </PhotoUpload>

                        <View style={styles.viewInput}>
                            <Text style={styles.textLabel}>ชื่อจริง</Text>

                            <Input
                                placeholder="ระบุชื่อจริง"
                                placeholderTextColor="#616161"
                                onChangeText={(text) => SetFirstname(text)}
                                value={firstname}/>
                            <Text style={styles.textLabel}>{"\n"}นามสกุล</Text>
                            <Input
                                placeholder="ระบุนามสกุล"
                                placeholderTextColor="#616161"
                                onChangeText={(text) => SetLastname(text)}
                                value={lastname}/>
                            <Text style={styles.textLabel}>{"\n"}อีเมลล์</Text>

                            <Input
                                placeholder="ระบุอีเมลล์"
                                placeholderTextColor="#616161"
                                onChangeText={(text) => SetEmail(text)}
                                value={email}/>
                            <Text style={styles.textLabel}>{"\n"}เบอร์โทรศัพท์</Text>

                            <Input
                                placeholder="ระบุเบอร์โทรศัพท์"
                                placeholderTextColor="#616161"
                                onChangeText={(text) => SetPhoneNumber(text)}
                                value={phonenumber}/>
                        </View>
                    </View>
                </ProgressStep>
                <ProgressStep
                    label="บัตรประชาชน"
                    nextBtnTextStyle={buttonTextStyle}
                    previousBtnTextStyle={buttonTextStyle}
                    previousBtnText="ก่อนหน้า"
                    nextBtnText="ถัดไป">
                    <View
                        style={{
                            alignItems: 'center'
                        }}>
                        <View style={styles.viewIDCard}>
                        <PhotoUpload
                            onPhotoSelect={avatar => {
                                if (avatar) {
                                    console.log('Image base64 string: ', avatar)
                                }
                            }}>
                            <Image
                                style={{
                                    paddingVertical: 30,
                                    width: 300,
                                    height: 200
                                }}
                                resizeMode='cover'
                                source={require('../assets/images/IDCard_Front.png')}/>
                        </PhotoUpload>
                        <Text>
                            {"\n"}
                        </Text>
                        <PhotoUpload
                            onPhotoSelect={avatar => {
                                if (avatar) {
                                    console.log('Image base64 string: ', avatar)
                                }
                            }}>
                            <Image
                                style={{
                                    paddingVertical: 30,
                                    width: 300,
                                    height: 200
                                }}
                                resizeMode='cover'
                                source={require('../assets/images/IDCard_Back.png')}/>
                        </PhotoUpload>
                     
                        </View>
                    </View>
                </ProgressStep>
                <ProgressStep label="ชื่อผู้ใช้" onSubmit={() => RegisterUser()}
                    nextBtnTextStyle={buttonTextStyle} previousBtnTextStyle={buttonTextStyle} previousBtnText="ก่อนหน้า" finishBtnText="สมัครสมาชิก">
                    <View
                        style={{
                            alignItems: 'center'
                        }}>
                        <View style={styles.viewInput}>
                            <Text style={styles.textLabel}>ชื่อผู้ใช้</Text>
                            <Input
                                placeholder="ระบุชื่อผู้ใช้"
                                placeholderTextColor="#616161"
                                onChangeText={(text) => SetUsername(text)}
                                value={username}/> 
                            <Text style={styles.textLabel}>{"\n"}รหัสผ่าน</Text>
                            <Input
                                secureTextEntry={true}
                                placeholder="ระบุรหัสผ่าน"
                                placeholderTextColor="#616161"
                                onChangeText={(text) => SetPassword(text)}
                                value={password}/>
                            <Text style={styles.textLabel}>{"\n"}ยีนยันรหัสผ่าน</Text>
                            <Input
                                secureTextEntry={true}
                                placeholder="ยืนยันรหัสผ่าน"
                                placeholderTextColor="#616161"
                                onChangeText={(text) => SetConfirmPassword(text)}
                                value={confirmpassword}/>
                        </View>
                    </View>
                </ProgressStep>
            </ProgressSteps>
        </View>
    );
};

